mapper.insert:
INSERT INTO MEMBER ( USER_ID
					, USER_PWD
					, USER_NAME
					, EMAIL
					, AGE
					, PHONE
					, ADDRESS
					, GENDER
					)
		VALUES ( #{userId}
				, #{userPwd}
				, #{userName}
				, #{email}
				, #{age}
				, #{phone}
				, #{address}
				, #{gender}
				)

mapper.update:
UPDATE MEMBER
   SET USER_NAME = #{userName}
     , EMAIL = #{email}
     , AGE = #{age}
     , PHONE = #{phone}
     , ADDRESS = #{address}
     , GENDER = #{gender}
 WHERE USER_ID = #{userId} AND STATUS = 'Y'
